mapper.select:
SELECT citizen_id as citizenId ,citizen_pw as citizenPw ,citizen_name as citizenName ,sigungu_name as sigunguName ,sido_name as sidoName ,eupmyeon_name as eupmyeonName ,citizen_address_sangse as citizenAddressSangse ,citizen_phone as citizenPhone ,citizen_email as citizenEmail FROM citizen
WHERE citizen_id = #{keyword}
WHERE citizen_name = #{keyword}
WHERE citizen_email = #{keyword}
ORDER BY citizen_id DESC LIMIT #{beginRow}, #{pagePerRow}

SELECT 
	COUNT(*)
FROM
	citizen

	
		
			WHERE citizen_id = #{keyword}
		
		
			WHERE citizen_name = #{keyword}
		
		
			WHERE citizen_email = #{keyword}

UPDATE
	citizen
SET
	citizen_pw = #{citizenPw}
	,sigungu_code = #{sigunguCode}
	,eupmyeon_code = #{eupmyeonCode}
	,citizen_address_sangse = #{citizenAddressSangse}
	,citizen_phone = #{citizenPhone}
	,citizen_email = #{citizenEmail}
WHERE
	citizen_id = #{citizenId}
