mapper.select:
SELECT gf.goodsfacility_code AS goodsfacilityCode ,gf.goodsfacility_classify_goodsfacility AS goodsfacilityClassifyGoodsfacility ,gfthree.goodsfacility_three_name AS goodsfacilityThreeName ,gf.goodsfacility_name AS goodsfacilityName ,delivorderciti.classify_rental_state AS classifyRentalState ,delivorderciti.citizen_id AS citizenId ,delivorderciti.send_address_sido AS sendAddressSido ,delivorderciti.send_address_sigungu AS sendAddressSigungu ,delivorderciti.send_address_eupmyeon AS sendAddressEupmyeon ,delivorderciti.send_address_sangse AS sendAddressSangse ,delivorderciti.receive_address_sido AS receiveAddressSido ,delivorderciti.receive_address_sigungu AS receiveAddressSigungu ,delivorderciti.receive_address_eupmyeon AS receiveAddressEupmyeon ,delivorderciti.receive_address_sangse AS receiveAddressSangse FROM delivery_order_citizen AS delivorderciti INNER JOIN goodsfacility AS gf ON delivorderciti.goodsfacility_code = gf.goodsfacility_code
INNER JOIN goodsfacility_three AS gfthree ON gf.goodsfacility_three_code = gfthree.goodsfacility_three_code
WHERE delivorderciti.delivery_order_citizen_code = ${deliveryOrderCitizenCode};

SELECT
	COUNT(*)
FROM
	delivery_order_citizen AS delivorderciti
	INNER JOIN
	adminagency AS admina
	ON
	delivorderciti.adminagency_code = admina.adminagency_code
	
	INNER JOIN
	goodsfacility AS gf
	ON
	delivorderciti.goodsfacility_code = gf.goodsfacility_code
	
	INNER JOIN
	goodsfacility_three AS gfthree
	ON
	gf.goodsfacility_three_code = gfthree.goodsfacility_three_code
WHERE
	delivorderciti.adminagency_code = 1234
	AND
	delivorderciti.is_canceled_delivery = 'N'
	AND
	delivorderciti.is_functionary_accept_order = 'N'
	AND
	gf.goodsfacility_state_afterservice = '일반'
	
		
			
				AND delivorderciti.delivery_order_citizen_code = #{searchWord}
			
			
				AND delivorderciti.goodsfacility_rental_code = #{searchWord}
			
			
				AND admina.adminagency_code = #{searchWord}
			
			
				AND admina.adminagency_name = #{searchWord}
			
			
				AND gf.goodsfacility_code = #{searchWord}
			
			
				AND gf.goodsfacility_classify_goodsfacility = #{searchWord}
			
			
				AND gfthree.goodsfacility_three_code = #{searchWord}
			
			
				AND gfthree.goodsfacility_three_name = #{searchWord}
			
			
				AND gf.goodsfacility_name = #{searchWord}
			
			
				AND delivorderciti.classify_rental_state = #{searchWord}
			
			
				AND delivorderciti.citizen_id = #{searchWord}
			
			
				AND delivorderciti.is_canceled_delivery = #{searchWord}
			
			
				AND delivorderciti.delivery_order_citizen_date = #{searchWord}

SELECT delivorderciti.delivery_order_citizen_code AS deliveryOrderCitizenCode ,delivorderciti.goodsfacility_rental_code AS goodsfacilityRentalCode ,admina.adminagency_code AS adminagencyCode ,admina.adminagency_name AS adminagencyName ,gf.goodsfacility_code AS goodsfacilityCode ,gf.goodsfacility_classify_goodsfacility AS goodsfacilityClassifyGoodsfacility ,gfthree.goodsfacility_three_code AS goodsfacilityThreeCode ,gfthree.goodsfacility_three_name AS goodsfacilityThreeName ,gf.goodsfacility_name AS goodsfacilityName ,delivorderciti.classify_rental_state AS classifyRentalState ,delivorderciti.citizen_id AS citizenId ,delivorderciti.send_address_sido AS sendAddressSido ,delivorderciti.send_address_sigungu AS sendAddressSigungu ,delivorderciti.send_address_eupmyeon AS sendAddressEupmyeon ,delivorderciti.send_address_sangse AS sendAddressSangse ,delivorderciti.receive_address_sido AS receiveAddressSido ,delivorderciti.receive_address_sigungu AS receiveAddressSigungu ,delivorderciti.receive_address_eupmyeon AS receiveAddressEupmyeon ,delivorderciti.receive_address_sangse AS receiveAddressSangse ,delivorderciti.is_canceled_delivery AS isCanceledDelivery ,delivorderciti.delivery_order_citizen_date AS deliveryOrderCitizenDate FROM delivery_order_citizen AS delivorderciti INNER JOIN adminagency AS admina ON delivorderciti.adminagency_code = admina.adminagency_code
INNER JOIN goodsfacility AS gf ON delivorderciti.goodsfacility_code = gf.goodsfacility_code
INNER JOIN goodsfacility_three AS gfthree ON gf.goodsfacility_three_code = gfthree.goodsfacility_three_code WHERE delivorderciti.adminagency_code = 1234 AND delivorderciti.is_canceled_delivery = 'N' AND delivorderciti.is_functionary_accept_order = 'N' AND gf.goodsfacility_state_afterservice = '일반'
AND delivorderciti.delivery_order_citizen_code = #{searchWord}
AND delivorderciti.goodsfacility_rental_code = #{searchWord}
AND admina.adminagency_code = #{searchWord}
AND admina.adminagency_name = #{searchWord}
AND gf.goodsfacility_code = #{searchWord}
AND gf.goodsfacility_classify_goodsfacility = #{searchWord}
AND gfthree.goodsfacility_three_code = #{searchWord}
AND gfthree.goodsfacility_three_name = #{searchWord}
AND gf.goodsfacility_name = #{searchWord}
AND delivorderciti.classify_rental_state = #{searchWord}
AND delivorderciti.citizen_id = #{searchWord}
AND delivorderciti.is_canceled_delivery = #{searchWord}
AND delivorderciti.delivery_order_citizen_date = #{searchWord}
ORDER BY delivorderciti.delivery_order_citizen_date DESC LIMIT #{beginRow}, #{pagePerRow}
