mapper.update:

UPDATE functionary
		SET
	functionary_id = #{functionaryId}
	,adminagency_code = #{adminagencyCode}
	,functionary_name = #{functionaryName}
	,functionary_phone = #{functionaryPhone}
	,functionary_email = #{functionaryEmail}
WHERE functionary_id=#{functionaryId}

mapper.select:
SELECT goodsfacility.goodsfacility_code as goodsfacilityCode ,goodsfacility.goodsfacility_name as goodsfacilityName ,goodsfacility.goodsfacility_purchaseprice as goodsfacilityPurchaseprice ,goodsfacility.goodsfacility_price_rental as goodsfacilityPriceRental ,goodsfacility.adminagency_code as adminagencyCode ,goodsfacility.functionary_id as functionaryId ,adminagency.adminagency_name as adminagencyName ,goodsfacility.goodsfacility_classify_goodsfacility as goodsfacilityClassifyGoodsfacility ,goodsfacility.goodsfacility_classify_donationpurchase as goodsfacilityClassifyDonationpurchase ,goodsfacility.goodsfacility_is_extra as goodsfacilityIsExtra ,goodsfacility.goodsfacility_is_possible_delivery as goodsfacilityIsPossibleDelivery ,goodsfacility.goodsfacility_state_afterservice as goodsfacilityStateAfterservice ,goodsfacility.goodsfacility_is_possible_rental as goodsfacilityIsPossibleRental ,goodsfacility.goodsfacility_date_registration as goodsfacilityDateRegistration FROM goodsfacility INNER JOIN adminagency using(adminagency_code) WHERE goodsfacility.functionary_id = #{functionaryDto.functionaryId}
AND (goodsfacility.goodsfacility_name like CONCAT('%', #{keyword}, '%') OR adminagency.adminagency_name like CONCAT('%', #{keyword}, '%'))
AND goodsfacility.goodsfacility_name like CONCAT('%', #{keyword}, '%')
AND adminagency.adminagency_name like CONCAT('%', #{keyword}, '%')
ORDER BY goodsfacility.goodsfacility_date_registration DESC LIMIT #{beginRow}, #{pagePerRow}

SELECT return_goodsfacility_info.return_goodsfacility_info_code as returnGoodsfacilityInfoCode ,return_goodsfacility_info.goodsfacility_code as goodsfacilityCode ,goodsfacility.goodsfacility_name as goodsfacilityName ,return_goodsfacility_info.classify_goodsfacility as classifyGoodsfacility ,return_goodsfacility_info.citizen_id as citizenId ,citizen.citizen_name as citizenName ,return_goodsfacility_info.is_requested_to_return_as_delivery as isRequestedToReturnAsDelivery ,return_goodsfacility_info.overdue_days as overdueDays ,return_goodsfacility_info.adminagency_code as adminagencyCode ,adminagency.adminagency_name as adminagencyName ,return_goodsfacility_info.state_goods_code as stateGoodsCode ,return_goodsfacility_info.date_return_check as dateReturnCheck from return_goodsfacility_info inner join goodsfacility using(goodsfacility_code) inner join adminagency on return_goodsfacility_info.adminagency_code = adminagency.adminagency_code inner join citizen using(citizen_id) where return_goodsfacility_info.functionary_id = #{functionaryDto.functionaryId}
AND (goodsfacility.goodsfacility_name like CONCAT('%', #{keyword}, '%') OR adminagency.adminagency_name like CONCAT('%', #{keyword}, '%')) OR citizen.citizen_name like CONCAT('%', #{keyword}, '%')
AND goodsfacility.goodsfacility_name like CONCAT('%', #{keyword}, '%')
AND adminagency.adminagency_name like CONCAT('%', #{keyword}, '%')
AND citizen.citizen_name like CONCAT('%', #{keyword}, '%')
ORDER BY return_goodsfacility_info.date_return_check DESC LIMIT #{beginRow}, #{pagePerRow}

SELECT agency.agency_code as agencyCode ,agency.adminagency_code as adminagencyCode ,adminagency.adminagency_name as adminagencyName ,agency.functionary_name as functionaryName ,functionary.functionary_id as functionaryId ,agency.agency_name as agencyName ,agency.agency_businesstype_code as agencyBusinesstypeCode ,agency.agency_date_registration as agencyDateRegistration ,agency.agency_date_contract_closed as agencyDateContractClosed from functionary inner join agency using(functionary_name) inner join adminagency on agency.adminagency_code = adminagency.adminagency_code where functionary.functionary_id = #{functionaryDto.functionaryId}
AND (adminagency.adminagency_name like CONCAT('%', #{keyword}, '%') OR agency.agency_name like CONCAT('%', #{keyword}, '%'))
AND adminagency.adminagency_name like CONCAT('%', #{keyword}, '%')
AND agency.agency_name like CONCAT('%', #{keyword}, '%')
ORDER BY agency.agency_date_registration DESC LIMIT #{beginRow}, #{pagePerRow}

SELECT annualfee_pakage.annualfee_pakage_code ,annualfee_pakage.annualfee_pakage_name ,annualfee_pakage_authority.adminagency_code ,adminagency.adminagency_name ,annualfee_pakage.annualfee_pakage_date_registration from annualfee_pakage inner join annualfee_pakage_authority using(annualfee_pakage_code) inner join adminagency using(adminagency_code) where annualfee_pakage.functionary_id = #{functionaryDto.functionaryId}
AND (annualfee_pakage.annualfee_pakage_name like CONCAT('%', #{keyword}, '%') OR adminagency.adminagency_name like CONCAT('%', #{keyword}, '%'))
AND annualfee_pakage.annualfee_pakage_name like CONCAT('%', #{keyword}, '%')
AND adminagency.adminagency_name like CONCAT('%', #{keyword}, '%')
AND annualfee_pakage.annualfee_pakage_date_registration like CONCAT('%', #{keyword}, '%')
ORDER BY annualfee_pakage.annualfee_pakage_date_registration DESC LIMIT #{beginRow}, #{pagePerRow}
