  				,goodsfacility_is_possible_rental
  				,goodsfacility_date_registration)
  			VALUE (#{goodsfacilityThreeCode}
  					,#{goodsfacilityName}
  					,#{goodsfacilityPurchaseprice}
  					,#{goodsfacilityPriceRental}
