  					,#{goodsfacilityClassifyDonationpurchase}
  					,#{goodsfacilityIsExtra}
  					,#{goodsfacilityIsPossibleDelivery}
  					,#{goodsfacilityStateAfterservice}
  					,#{goodsfacilityIsPossibleRental}
  					,now())

mapper.select:
SELECT goodsfacility.goodsfacility_code ,goodsfacility.goodsfacility_three_code ,goodsfacility.goodsfacility_name ,goodsfacility.adminagency_code ,goodsfacility.goodsfacility_address_sangse ,goodsfacility.goodsfacility_is_possible_rental ,goodsfacility.goodsfacility_is_possible_delivery ,goodsfacility.goodsfacility_price_rental ,goodsfacility_file.goodsfacility_file_name ,goodsfacility_file.goodsfacility_file_ext ,goodsfacility_file.goodsfacility_file_type ,goodsfacility_file.goodsfacility_file_size FROM goodsfacility JOIN goodsfacility_file ON goodsfacility.goodsfacility_code = goodsfacility_file.goodsfacility_code
WHERE goodsfacility_name = #{keyword}
WHERE goodsfacility_address_sangse = #{keyword}
WHERE goodsfacility_is_possible_delivery = #{keyword}
WHERE goodsfacility_is_possible_rental = #{keyword}
WHERE goodsfacility_three_code = #{keyword}
ORDER BY goodsfacility_name DESC LIMIT #{beginRow}, #{pagePerRow}

  			SELECT
  				COUNT(*)
  			FROM
  				goodsfacility
			
				
					
						WHERE goodsfacility_name = #{keyword}
					
					
						WHERE goodsfacility_address_sangse = #{keyword}
					
					
						WHERE goodsfacility_is_possible_delivery = #{keyword}
					
					
						WHERE goodsfacility_is_possible_rental = #{keyword}
					
					
						WHERE goodsfacility_three_code = #{keyword}

SELECT goodsfacility.goodsfacility_code ,goodsfacility.goodsfacility_name ,goodsfacility_file.goodsfacility_file_name ,goodsfacility_file.goodsfacility_file_ext FROM goodsfacility JOIN goodsfacility_file ON goodsfacility.goodsfacility_code = goodsfacility_file.goodsfacility_code
WHERE goodsfacility_name = #{keyword}
ORDER BY goodsfacility_name DESC LIMIT #{beginRow}, #{pagePerRow}
