mapper.insert:
  			
			SELECT LAST_INSERT_ID()
			
  			
  			INSERT INTO goodsfacility_rental
  					(adminagency_code
  					,goodsfacility_code
  					,goodsfacility_name
  					,citizen_id
  					,citizen_name
  					,citizen_phone
  					,goodsfacility_rental_date_start
  					,goodsfacility_rental_day_end
  					,goodsfacility_rental_is_ordered_delivery
  					,goodsfacility_rental_date_registration
  					,goodsfacility_rental_is_canceled
  					,goodsfacility_rental_deposit)
  				VALUE(#{adminagencyCode}
  					 ,#{goodsfacilityCode}
  					 ,#{goodsfacilityName}
  					 ,#{citizenId}
  					 ,#{citizenName}
  					 ,#{citizenPhone}
  					 ,#{goodsfacilityRentalDateStart}
  					 ,#{goodsfacilityRentalDayEnd}
  					 ,#{goodsfacilityRentalIsOrderedDelivery}
  					 ,now()
  					 ,#{goodsfacilityRentalIsCanceled}
  					 ,#{goodsfacilityRentalDeposit})

mapper.select:
SELECT goodsfacility_rental_code as goodsfacilityRentalCode ,adminagency_code as adminagencyCode ,citizen_id as citizenId ,citizen_phone as citizenPhone ,goodsfacility_code as goodsfacilityCode ,goodsfacility_name as goodsfacilityName ,goodsfacility_rental_date_start as goodsfacilityRentalDateStart ,goodsfacility_rental_day_end as goodsfacilityRentalDayEnd ,goodsfacility_rental_is_ordered_delivery as goodsfacilityRentalIsOrderedDelivery ,goodsfacility_rental_date_registration as goodsfacilityRentalDateRegistration ,goodsfacility_rental_is_canceled as goodsfacilityRentalIsCanceled FROM goodsfacility_rental WHERE citizen_id = #{citizenDto.citizenId}
WHERE citizen_id = #{keyword}
WHERE adminagency_code = #{keyword}
WHERE goodsfacility_rental_date_registration = #{keyword}
ORDER BY citizen_id DESC LIMIT #{beginRow}, #{pagePerRow}

SELECT 
	COUNT(*)
FROM
	goodsfacility_rental

	
		
			WHERE citizen_id = #{keyword}
							
		
			WHERE adminagency_code = #{keyword}
		
		
			WHERE goodsfacility_rental_date_registration = #{keyword}

mapper.update:
UPDATE
	goodsfacility_rental
SET
	citizen_phone = #{citizenPhone}
WHERE
	goodsfacility_rental_code = #{goodsfacilityRentalCode}

