mapper.select:
select return_goodsfacility_info.return_goodsfacility_info_code as returGoodsfacilityInfoCode ,return_goodsfacility_info.goodsfacility_rental_code as goodsfacilityRentalCode ,return_goodsfacility_info.goodsfacility_code as goodsfacilityCode ,goodsfacility.goodsfacility_name as goodsfacilityName ,return_goodsfacility_info.classify_goodsfacility as classifyGoodsfacility ,return_goodsfacility_info.citizen_id as citizenId ,citizen.citizen_name as citizenName ,return_goodsfacility_info.is_requested_to_return_as_delivery as isRequestedToReturnAsDelivery ,return_goodsfacility_info.overdue_days as overdueDays ,return_goodsfacility_info.adminagency_code as adminagencyCode ,return_goodsfacility_info.functionary_id as functionaryId ,functionary.functionary_name as functionaryName ,return_goodsfacility_info.state_goods_code as stateGoodsCode ,st.state_goods as stateGoods ,return_goodsfacility_info.date_return_check as dateReturnCheck from return_goodsfacility_info join goodsfacility using(goodsfacility_code) join citizen using(citizen_id) join functionary on return_goodsfacility_info.functionary_id = functionary.functionary_id join state_goods as st using(state_goods_code)
where (goodsfacility.goodsfacility_name like CONCAT('%', #{keyword}, '%') or citizen.citizen_name like CONCAT('%', #{keyword}, '%') or functionary.functionary_name like CONCAT('%', #{keyword}, '%'))
where goodsfacility.goodsfacility_name like CONCAT('%', #{keyword}, '%')
where citizen.citizen_name like CONCAT('%', #{keyword}, '%')
where functionary.functionary_name like CONCAT('%', #{keyword}, '%')
where return_goodsfacility_info.classify_goodsfacility like CONCAT('%', #{keyword}, '%')
where return_goodsfacility_info.is_requested_to_return_as_delivery = #{keyword}
where st.state_goods like CONCAT('%', #{keyword}, '%')
WHERE return_goodsfacility_info.date_return_check BETWEEN #{dateDto.startDate} AND #{dateDto.endDate}
and return_goodsfacility_info.adminagency_code = #{adminagencyCode}
and return_goodsfacility_info.citizen_id = #{citizenId}
ORDER BY return_goodsfacility_info.date_return_check DESC LIMIT #{beginRow}, #{pagePerRow}
